SUM(
    CASE
      WHEN (
        SAFE_CAST(
          CASE
            WHEN (
              DATETIME_SUB(DATE('2025-05-28', 'UTC'), INTERVAL -7 DAY) >= SAFE_CAST(t0.fy_first_day_of_week AS DATETIME)
            ) AND (
